#!/bin/bash
# MiniFi - Deploy Both Frontend & Backend
# Deploys frontend to Vercel and triggers backend deployment on Render

set -e

echo "🚀 MiniFi - Full Deployment"
echo "============================"
echo ""

# Colors for output
GREEN='\033[0;32m'
BLUE='\033[0;34m'
YELLOW='\033[1;33m'
RED='\033[0;31m'
NC='\033[0m' # No Color

# Default backend URL
DEFAULT_BACKEND_URL="https://minifi-backend.onrender.com"

# Get backend URL (optional, can use default)
echo -e "${BLUE}📋 Backend Configuration${NC}"
echo "Backend URL (press Enter for default: $DEFAULT_BACKEND_URL):"
read -p "> " BACKEND_URL
BACKEND_URL=${BACKEND_URL:-$DEFAULT_BACKEND_URL}
echo ""

# =============================================================================
# FRONTEND DEPLOYMENT (Vercel)
